import Yoga, { EDGE_BOTTOM, EDGE_LEFT, EDGE_RIGHT, EDGE_TOP, YogaNode } from "yoga-layout-prebuilt"
import enumLookups from "./enum-lookups"

type EdgeProperties = keyof {
    [Key in keyof YogaNode as YogaNode[Key] extends (...args: infer X) => any
        ? X["length"] extends 2
            ? Uncapitalize<RemovePercentageAndAuto<TrimSet<Key>>>
            : never
        : never]: YogaNode[Key]
}

type CamelCaseFromSnakeCase<T extends string> = T extends `${infer M}_${infer N}`
    ? `${CamelCaseFromSnakeCase<M>}${Capitalize<CamelCaseFromSnakeCase<N>>}`
    : Lowercase<T>

type AlignType = GetEnumTypes<"align">

type Enums = {
    alignContent: AlignType
    alignItems: AlignType
    alignSelf: AlignType
    display: GetEnumTypes<"display">
    flexDirection: GetEnumTypes<"flex_direction">
    flexWrap: GetEnumTypes<"wrap">
    overflow: GetEnumTypes<"overflow">
    justifyContent: GetEnumTypes<"justify">
    positionType: GetEnumTypes<"position_type">
}

export const enumsToPrefix: { [Key in keyof Enums]: string } = {
    alignContent: "align",
    alignItems: "align",
    alignSelf: "align",
    display: "display",
    flexDirection: "flexDirection",
    flexWrap: "wrap",
    justifyContent: "justify",
    overflow: "overflow",
    positionType: "positionType",
}

export const snakeCaseFromCamelCase = (str: string) =>
    str.replace(/[A-Z]/g, (letter) => `_${letter.toLowerCase()}`).toUpperCase()

export function translateEnums<Name extends string>(
    name: Name,
    value: any
): Name extends keyof Enums ? Enums[Name] : any {
    if (isKeyOf(name, enumsToPrefix)) {
        const prefix = enumsToPrefix[name]
        const key = `${snakeCaseFromCamelCase(prefix)}_${snakeCaseFromCamelCase(value)}`
        const constant = (Yoga as any)[key]
        if (constant == null) {
            throw `unkown value "${value}" for property "${name}"`
        }
        return constant
    }
    return value
}

export function retranslateEnum(name: string, value: any): any {
    if (isKeyOf(name, enumLookups)) {
        const lookup = enumLookups[name]
        if (isKeyOf(value, lookup)) {
            return lookup[value]
        }
        throw `can't retranslate value "${value}" of property "${name}"`
    }
    return value
}

function isKeyOf<T>(key: any, value: T): key is keyof T {
    return key in value
}

export const edgeToConstant = {
    Top: EDGE_TOP,
    Bottom: EDGE_BOTTOM,
    Left: EDGE_LEFT,
    Right: EDGE_RIGHT,
}

export type RemoveEdge<T extends keyof YogaNodeProperties> = T extends AllEdges<infer K> ? K : T

type GetEnumTypes<Prefix extends string> = GetEnumTypes_<keyof typeof Yoga, Prefix>
type GetEnumTypes_<T, Prefix extends string> = T extends `${Uppercase<Prefix>}_${infer T}`
    ? CamelCaseFromSnakeCase<T>
    : never

export type YogaNodeProperties = {
    [Key in Exclude<Uncapitalize<RemovePercentageAndAuto<TrimSet<keyof YogaNode>>>, EdgeProperties | "flex">]:
        | GetType<`set${Capitalize<Key>}`, YogaNode, 0>
        | undefined
} & {
    [Key in AllEdges<EdgeProperties>]: GetType<`set${Capitalize<RemoveEdge<Key>>}`, YogaNode, 1> | undefined
}

type GetType<T extends keyof O, O, index extends 0 | 1> = T extends `set${Capitalize<keyof Enums>}`
    ? Enums[Uncapitalize<TrimSet<T>>]
    : O[T] extends (...args: infer K) => any
    ? K[index]
    : never
type AllEdges<T extends string> = `${T}${"Top" | "Bottom" | "Right" | "Left"}`
type RemovePercentageAndAuto<Name extends string> = Name extends `${string}${"Percent" | "Auto"}` ? never : Name
type TrimSet<Name extends string> = Name extends `set${infer X}` ? X : never

import { YogaNode, Node } from "yoga-layout-prebuilt"
import { edgeToConstant, RemoveEdge, retranslateEnum, translateEnums, YogaNodeProperties } from "."
import nodeDefaults from "./node-defaults"

const edgeRegex = /^(.+)(Top|Bottom|Left|Right)$/

export class FlexNode {
    private readonly node: YogaNode
    private readonly children: Set<FlexNode> = new Set([])
    public index: number | undefined

    constructor(private readonly onCalc: () => void) {
        this.node = Node.create()
    }

    destroy(): void {
        this.node.free()
    }

    commitChanges() {
        //TODO
        this.children.forEach((child) => {})
    }

    private onCalculated(): void {
        this.onCalc()
        this.children.forEach((child) => child.onCalc())
    }

    insertChild(node: FlexNode): void {
        this.children.add(node)
    }

    removeChild(node: FlexNode): void {
        this.children.delete(node)
    }

    setProperty<Name extends keyof YogaNodeProperties>(name: Name, value: YogaNodeProperties[Name]): void {
        if (value == null && name === "measureFunc") {
            this.node.unsetMeasureFunc()
            return
        }
        this.setRawProperty(
            name,
            value == null ? nodeDefaults[name as keyof typeof nodeDefaults] : translateEnums(name, value)
        )
    }

    private callNodeFunction<Prefix extends "get" | "set", Name extends keyof YogaNodeProperties>(
        prefix: Prefix,
        name: Name,
        ...params: Array<any>
    ) {
        const edgeMatch = edgeRegex.exec(name)
        const key = (edgeMatch == null ? name : edgeMatch[1]) as RemoveEdge<Name>
        const edgeProperties = edgeMatch == null ? [] : [edgeToConstant[edgeMatch[2] as keyof typeof edgeToConstant]]
        if (key == "measureFunc" && prefix === "get") {
            throw "getProperty 'measureFunc' is not possible"
        }
        const fnName: `${Prefix}${Capitalize<Exclude<RemoveEdge<Name>, "measureFunc">>}` = `${prefix}${capitalize(
            key as Exclude<typeof key, "measureFunc">
        )}`
        const func: Function = this.node[fnName]
        if (func == null) {
            throw `property "${name}" is not exisiting`
        }
        return func.call(this.node, ...(edgeProperties as []), ...params)
    }

    private setRawProperty = this.callNodeFunction.bind(this, "set")

    getProperty<Name extends keyof YogaNodeProperties>(name: Name): YogaNodeProperties[Name] {
        let value: YogaNodeProperties[Name] | { value: YogaNodeProperties[Name] } = this.getRawProperty(name)
        if (typeof value === "object") {
            if ("value" in value) {
                value = value.value
            } else {
                throw `unknown return value "${value}" for getting property "${name}"`
            }
        }
        if (typeof value === "number" && isNaN(value)) {
            return undefined
        }
        return retranslateEnum(name, value)
    }

    private getRawProperty = this.callNodeFunction.bind(this, "get")
}

function capitalize<Key extends string>(key: Key) {
    return `${key.charAt(0).toUpperCase()}${key.slice(1)}` as Capitalize<Key>
}
